import java.util.*;import java.lang.*; //=============================================================================////                           CLASSE CONNECTION//=============================================================================//class Connection{    //------------------------------------------------------    //                    CHAMPS    //------------------------------------------------------    public TermNode depart;    public ClassNode arrivee;    public float valeur;        //-------------------------------------------------------    //                    METHODES    //-------------------------------------------------------    // ------------------------------------------    // classe      : connection    // nom         : connection    // entree(s)   : noeud n1 : noeud de depart, noeud n2 : noeud d'arrivee    // sortie(s)   : retourne la connection construite    // description : constructeur    // remarque    :     // ------------------------------------------    public Connection(TermNode n1,ClassNode n2)     {		this.depart = n1;		this.arrivee = n2;		valeur = 0;    }    // ------------------------------------------    // classe      : connection    // nom         : afficher    // entree(s)   : reseau r : objet central regroupant un certain nombre de methodes utiles    //               dont la methode convertir    // sortie(s)   : void    // description : affichage des informations propre a la connection    // remarque    :     // ------------------------------------------    public void afficher()    {   	    System.out.print(depart.id+"---> class: "+valeur);    }        // ------------------------------------------    // classe      : connection    // nom         : mise_a_jour    // entree(s)   :     // sortie(s)   : void    // description : mise a jour de la connection en fonction des valeur d'activation    //		     des noeuds de depart et de sortie (regle d'association)    // remarque    :     // ------------------------------------------    public void mise_a_jour()    {    	valeur = valeur + depart.activation*arrivee.activation;    }}// Class connection//=============================================================================//