import java.util.*;import java.io.*;//=============================================================================////                                 CLASSE Node//=============================================================================//public abstract class Node {    //------------------------------------------------------    //                    CHAMPS    //------------------------------------------------------    public float activation;  // activation courante du noeud    public float somme;     // somme des activations sur l'ensemble d'apprentissage    public Network net;    //-------------------------------------------------------    //                    METHODES    //-------------------------------------------------------    abstract void activer();    // ------------------------------------------    // classe      : Node    // nom         : Node    // entree(s)   :     // sortie(s)   : le noeud construit     // description : constructeur de base    // remarque    :     // ------------------------------------------    public Node(Network n) {        activation = 0;        somme = 0;        net = n;    }    // ------------------------------------------    // classe      : Node    // nom         : afficher    // entree(s)   :     // sortie(s)   : affiche les caracteristiques du noeud    // description :     // remarque    :     // ------------------------------------------    public void afficher() {        System.out.println("activation: " + activation);        System.out.println("somme: " + somme);    }    // ------------------------------------------    // classe      : Node    // nom         : desactiverL    // entree(s)   :     // sortie(s)   :     // description : incremente la somme et remet l'activation à 0    //     // remarque    : utilisée dans la phase d'apprentissage    // ------------------------------------------    public void desactiverL() {        somme = somme + activation;        activation = 0;    }    // ------------------------------------------    // classe      : node    // nom         : reinitialise_cp    // entree(s)   :     // sortie(s)   :     // description : remet l'activation à 0    //     // remarque    : utilisée dans la phase de classification    // ------------------------------------------    public void desactiver() {        activation = 0;    }}//=============================================================================//