import java.util.*;//=============================================================================////                           CLASSE LISTE_CONNECTIONS//=============================================================================//class ConnectionList extends ArrayList<Connection>{    //------------------------------------------------------    //                    CHAMPS    //------------------------------------------------------        //-------------------------------------------------------    //                    METHODES    //-------------------------------------------------------    public ConnectionList()     {		super();    }    // ------------------------------------------    // classe      :     // nom         : afficher    // entree(s)   :     // sortie(s)   :     // description :     // remarque    :     // ------------------------------------------    void afficher()    {		int i = 0;		int taille = size();		Connection c;		while (i<taille)		{	    	c = get(i);	    	c.afficher();	    	i++;		}    }    }//ConnectionList//=============================================================================//